import React from 'react'
// components
import Title from './Title'
import Paragraph from './Paragraph'
import { Stack } from "@mui/material";
// images
import hatMauImg from '../assets/hat_nhua_mau.jpeg'
import { Box } from '@mui/system';


const Content = () => {
  return (    
    <Stack
    direction='column'
    justifyContent= 'center'
    alignItems= 'center'
    sx={{
        py: 8,
        px: 2,
        display: { xs: 'flex'},
    }}
    >
        <Box
        component='section'
        sx={{
            paddingBottom: 3,
        }}
        >
            <Title 
            text={
                'SẢN PHẨM CỦA CHÚNG TÔI'
            }
            textAlign={'center'}
            />
            <Paragraph text={
                'Hồng Thịnh Phát chuyên sản xuất và cung cấp phụ gia, hạt màu, bột màu, chất tẩy trắng phục vụ ngành nhựa'
            } 
            maxWidth = {'sm'}
            mx={'auto'}
            textAlign={'center'}
            />
        </Box>

        <Box sx={{ 
            maxWidth: 700,
            width: '100%',
        }}>
            <img
                src={hatMauImg}
                alt="hatMauImg"
                style={{ 
                    width: "90%", 
                    marginBottom: "30%",
                }}
                />
        </Box>
    </Stack>
    );
}

export default Content
