import React from 'react'
import {  
    Grid
} from "@mui/material";
// components
import Title from './Title'
import Paragraph from './Paragraph'
// images
import hatMauImg from '../assets/hat_nhua_mau.jpeg'
import { Box } from '@mui/system';


const Content = () => {
  return (    
        <Grid container spacing={0}   
        sx={{
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'center',
            alignItems: 'center',
            py: 10,
            px: 2,
        }}
        >
            <Grid item xs={12} sm={12} md={5}
            component = 'section'
            >
                <Title
                text={
                    'SẢN PHẨM CỦA CHÚNG TÔI'
                }
                textAlign={'start'}
                />

                {/* <Typography 
                variant='h6'
                component='h4' 
                sx = {{
                    fontWeight: '400',
                    paddingTop: 1,
                }}
                >
                    Property facilities
                </Typography> */}

                <Paragraph 
                text={
                    ' Hồng Thịnh Phát chuyên sản xuất, và cung cấp phụ gia, hạt màu, bột màu, chất tẩy trắng phục vụ ngành nhựa'
                }
                maxWidth = {'75%'}
                mx={0}
                textAlign={'start'}
                />
            </Grid>
            <Box>
            <image src={hatMauImg} 
                        alt="hatMauImg"
                style={{ 
                    width: "90%", 
                    marginBottom: "30%",
                }}
                    />
            </Box>
        </Grid>
    );
}

export default Content
