import React from 'react'
// components
import Title from './Title'
import Paragraph from './Paragraph'
import { Stack, Typography } from "@mui/material";
// images
import hatMauImg from '../assets/PCV_Compound.jpeg'
import { Box } from '@mui/system';


const Content = () => {
  return (    
    <Stack
    direction='column'
    justifyContent= 'center'
    alignItems= 'center'
    sx={{
        py: 8,
        px: 2,
        display: { xs: 'flex'},
    }}
    >
        <Box
        component='section'
        sx={{
            paddingBottom: 3,
        }}
        >
            <Title 
            text={
                'SẢN PHẨM CỦA CHÚNG TÔI'
            }
            textAlign={'center'}
            />
            <Paragraph text={
                'Hồng Thịnh Phát chuyên sản xuất và cung cấp phụ gia, hạt màu, bột màu, chất tẩy trắng phục vụ ngành nhựa'
            } 
            maxWidth = {'sm'}
            mx={'auto'}
            textAlign={'center'}
            />
        </Box>

        <Box sx={{ 
            maxWidth: 800,
            width: '100%',

        }}>
          <Box marginRight='10%'>
            <img
              src={hatMauImg}
              alt="hatMauImg"
              style={{ 
                width: "100%",
                // height: "50%"
              }}
            />
            <Typography
                variant='h6'
                component= 'h6'
                >
                    Hạt Nhựa Màu
                </Typography>
          </Box>
          <Box>
            <img
              src={hatMauImg}
              alt="hatMauImg"
              style={{ 
                width: "100%",
              }}
            />
          </Box>
          <Box>
            <img
              src={hatMauImg}
              alt="hatMauImg"
              style={{ 
                width: "100%",
              }}
            />
          </Box>
        </Box>
    </Stack>
    );
}

export default Content
